mapper.select:
select idmember, rank_id, is_manager,is_delete, nickname, id, password, email, git_url, kakao_url,instagram_url ,date_created,update_created, grade 
from `coco`.`member` as m join `coco`.`rank` as r 
where  m.rank_id = r.idrank and idmember in (select member_id from `coco`.`like` where post_id = #{value});
