mapper.sql:
update member_tag set
access=#{access},
frequency=#{frequency}
where
idmember_tag=#{idmemberTag}

select.where:
access = ${access}

	AND
	idmember_tag = ${idmemberTag}


	AND
	member_id = ${memberId}


	AND
	tag_id = ${tagId}

delete.where:
access = ${access}

	AND
	idmember_tag = ${idmemberTag}


	AND
	member_id = ${memberId}


	AND
	tag_id = ${tagId}
