mapper.sql:
update mypage set

	member_id=#{memberId},


	banner_image_path=#{bannerImagePath},


	banner_text=#{bannerText},


	today_visited_count=#{todayVisitedCount},


	total_visited_count=#{totalVisitedCount}

access=#{access}
where
idmypage=#{idmypage}

select.where:
access = ${access}

	AND
	idmypage = ${idmypage}


	AND
	member_id = ${memberId}


	AND
	banner_image_path = '${bannerImagePath}'


	AND
	banner_text = '${bannerText}'

delete.where:
access = ${access}

	AND
	idmypage = ${idmypage}


	AND
	member_id = ${memberId}


	AND
	banner_image_path = '${bannerImagePath}'


	AND
	banner_text = '${bannerText}'
